    background-color: #3fa5dc;
    color: #fff;
}

.panel a {
    text-decoration: none;
    background-color: #ebebeb;
    transition: .5s;
    border-radius: 0;
    color: #283c4f;
    display: block;
    padding: 8px 15px 8px 35px;
    position: relative;
}

.in {
    visibility: visible !important;
}

p:last-child {
    margin-bottom: 0;
}

p {
    margin: 0 0 10px;
}

.block {
