}

.skillbar .skillbar-title {
    position: absolute;
    top: 0;
    left: 0;
    width: 115px;
    background-color: rgba(0, 0, 0, 0.2);
    color: #fff;
}

.skillbar .skill-bar-percent {
    position: absolute;
    right: 10px;
    top: 0;
