}
.panel a:before {
    background: #283c4f;
    content: "";
    height: 15px;
    left: 0;
    margin-left: 17px;
    margin-top: 11px;
    position: absolute;
    top: 0;
    width: 1px;
}
.panel a[aria-expanded="true"]:after {
    background: #ffffff !important;
}
