.skylark {
    width: 100%;
    height: 100%;
    z-index: 50;
    background: #f2f2f2;
}

.skylark .theme {
    background: #f9f9f9;
    background-color: rgba(0, 0, 0, 0);
    background-repeat: no-repeat;
    background-image: url(/src/assets/images/business-2717063_960_720.jpg);
    background-size: cover;
    background-position: center center;
    width: 100%;
    height: 500px;
}

.skylark h2 {
    display: inline-block;
    line-height: 1;
    position: relative;
    margin-bottom: 20px;
    padding-bottom: 15px;
    margin-top: 5px;
}

.skylark .lead {
    text-align: center;
    font-size: 20px;
    margin-left: auto;
    margin-right: auto;
}

.skylark .text {
    margin: 0 0 10px;
    font-size: 16px;
    line-height: 30px;
}

.skylark .text:last-child {
    margin-bottom: 0;
}

.skylark h2:before {
    width: 100%;
    height: 4px;
    display: block;
    position: absolute;
    bottom: 0;
    left: 0;
    content: "";
    background-color: #3fa5dc;
}

.skylark .features .icon {
    color: #3fa5dc;
    font-size: 30px;
}

.skylark .features .desc {
    display: block;
    color: #fff;
    font-size: 14px;
}

.skylark .features .leftTitle {
    color: #fff;
}

.skylark .themeText {
    font-size: 22px;
    font-weight：1000；
}

.skylark>.container {
    padding-bottom: 80px;
}

.skylark .container {
    padding-top: 70px;
}

ol,
ul {
    list-style: none;
}

.skylark .title {
    font-size: 50px;
    line-height: 58px;
    font-weight: 300;
    color: #333333;
    padding-top: 150px;
    font-weight: 800;
}

.skylark .custom-icon-list .title {
    font-size: initial;
}

@media (max-width: 767px) {
    .skylark .theme {
        height: 200px;
    }
    .skylark .title {
        padding-top: 60px;
    }
    .skylark .custom-icon-list .leftTitle{
      text-align:center;
    }
    .skylark .custom-icon-list li{
      text-align:center;
    }
    .skylark .custom-icon-list .title{
      display:block;
      padding-top:0;
    }
}

